<?php/** * @package  getwebThemeOption */namespace Getweb\Common;use Getweb\Common\BaseController;class CommonController extends BaseController {	public function register()	{		add_filter( "plugin_action_links_$this->plugin", array( $this, 'settings_link' ) );		add_action('admin_enqueue_scripts', array( $this, 'getweb_admin_style' ) );		add_action('admin_head', array( $this, 'getweb_option_admin_message' ) );	}	//Option Link	public function settings_link( $links )	{		$settings_link = '<a href="admin.php?page=getweb_theme_options">Settings</a>';		array_push( $links, $settings_link );		return $links;	}	//Admin Css	public function getweb_admin_style() {		?>		<style>			#toplevel_page_getweb_theme_options .wp-submenu-wrap li:nth-child(4),#toplevel_page_getweb_theme_options .wp-submenu-wrap li:nth-child(5),#toplevel_page_getweb_theme_options .wp-submenu-wrap li:nth-child(7),#toplevel_page_getweb_theme_options .wp-submenu-wrap li:nth-child(8){				display: none;			}		</style>	<?php }	/**	 * Admin Message	 */	public function getweb_option_admin_message() {		?>		<script type="text/javascript">          jQuery(function(){            var message = '<p>This theme comes with an <a href="<?php echo admin_url('admin.php?page=getweb_theme_options'); ?>">awesome theme option.</a> </p>';            jQuery('.themes-php #message2').html(message);          });		</script>		<?php	}}