<?php/** * @package  getwebThemeOption */namespace Getweb\Field;use Getweb\Api\SettingsApi;use Getweb\Api\Callbacks\OptionCallbacks;class OptionFieldController {	public $settings;	public $callbacks;	public $subpages = array();	public function register() {		$this->settings = new SettingsApi();		$this->callbacks = new OptionCallbacks();		$this->setSubpages();		$this->settings->addSubPages( $this->subpages )->register();	}	public function setSubpages() {		$this->subpages = array(			array(				'parent_slug' => 'getweb_theme_options',				'page_title'  => 'Manage Field',				'menu_title'  => 'Manage Field',				'capability'  => 'manage_options',				'menu_slug'   => 'getweb_option_fileds',				'callback'    => array( $this->callbacks, 'field_view' )			),			array(				'parent_slug' => 'getweb_theme_options',				'page_title'  => 'Add Field',				'menu_title'  => '',				'capability'  => 'manage_options',				'menu_slug'   => 'getweb_new_field',				'callback'    => array( $this->callbacks, 'field_new' )			),			array(				'parent_slug' => 'getweb_theme_options',				'page_title'  => 'Edit Field',				'menu_title'  => '',				'capability'  => 'manage_options',				'menu_slug'   => 'getweb_edit_field',				'callback'    => array( $this->callbacks, 'field_edit' )			),		);	}}