<?phpfunction getweb_menu_table(){	global $wpdb;	return $wpdb->prefix . "getweb_option_menu";}function getweb_field_table(){	global $wpdb;	return $wpdb->prefix . "getweb_option_field";}function get_fileds_id($id){	global $wpdb;	$field_by_id = $wpdb->get_row(		$wpdb->prepare(			"SELECT * from " . getweb_field_table() . " WHERE id = %d ",$id		),ARRAY_A	);	return $field_by_id;}function get_menus($publish = true){	global $wpdb;	if ($publish == 1) {		$get_headings = $wpdb->get_results(			$wpdb->prepare(				"SELECT * from " . getweb_menu_table() . " WHERE status = %d ",1			),ARRAY_A		);	} elseif ($publish == 0) {		$get_headings = $wpdb->get_results(			$wpdb->prepare(				"SELECT * from " . getweb_menu_table() . " WHERE status = %d ",0			),ARRAY_A		);	} elseif ($publish == -1) {		$get_headings = $wpdb->get_results(			$wpdb->prepare(				"SELECT * from " . getweb_menu_table() . " ORDER by id ASC", ""			), ARRAY_A		);	} else {		return 'Do not matched.';	}	return $get_headings;}function get_menu_by_id($id){	global $wpdb;	$field_by_id = $wpdb->get_row(		$wpdb->prepare(			"SELECT * from " . getweb_menu_table() . " WHERE id = %d ",$id		),ARRAY_A	);	return $field_by_id;}function menu_name_by_id( $id ) {	global $wpdb;	$getweb_heading_by_id = $wpdb->get_row(		$wpdb->prepare(			"SELECT * from " . getweb_menu_table() . " WHERE id = %d ", $id		), ARRAY_A	);	return $getweb_heading_by_id['name'];}function create_before( $time_ago ) {	$time_ago     = strtotime( $time_ago );	$cur_time     = time();	$time_elapsed = $cur_time - $time_ago;	$seconds      = $time_elapsed;	$minutes      = round( $time_elapsed / 60 );	$hours        = round( $time_elapsed / 3600 );	$days         = round( $time_elapsed / 86400 );	$weeks        = round( $time_elapsed / 604800 );	$months       = round( $time_elapsed / 2600640 );	$years        = round( $time_elapsed / 31207680 );	// Seconds	if ( $seconds <= 60 ) {		return "just now";	} //Minutes	elseif ( $minutes <= 60 ) {		if ( $minutes == 1 ) {			return "one minute ago";		} else {			return "$minutes minutes ago";		}	} //Hours	elseif ( $hours <= 24 ) {		if ( $hours == 1 ) {			return "an hour ago";		} else {			return "$hours hours ago";		}	} //Days	elseif ( $days <= 7 ) {		if ( $days == 1 ) {			return "yesterday";		} else {			return "$days days ago";		}	} //Weeks	elseif ( $weeks <= 4.3 ) {		if ( $weeks == 1 ) {			return "a week ago";		} else {			return "$weeks weeks ago";		}	} //Months	elseif ( $months <= 12 ) {		if ( $months == 1 ) {			return "a month ago";		} else {			return "$months months ago";		}	} //Years	else {		if ( $years == 1 ) {			return "one year ago";		} else {			return "$years years ago";		}	}}if(!function_exists('get_theme_option_value')){	function get_theme_option_value( $key, $default, $replace_site_url = false ) {		if( is_customize_preview() ) {			$value = get_theme_mod( $key, $default );			return $replace_site_url ?				str_replace( '[site_url]', get_home_url(), $value ) :				$value;			return $value;		}		global $smof_data;		$value = isset($smof_data[$key]) ? $smof_data[$key] : $default;		return $value;	}}