<?php/** * @package  getwebThemeOption */namespace Getweb\Menu;use Getweb\Api\SettingsApi;use Getweb\Api\Callbacks\OptionCallbacks;class OptionMenuController {	public $settings;	public $callbacks;	public $subpages = array();	public function register() {		$this->settings = new SettingsApi();		$this->callbacks = new OptionCallbacks();		$this->setSubpages();		$this->settings->addSubPages( $this->subpages )->register();	}	public function setSubpages() {		$this->subpages = array(			array(				'parent_slug' => 'getweb_theme_options',				'page_title'  => 'Option Menu',				'menu_title'  => 'Manage Menu',				'capability'  => 'manage_options',				'menu_slug'   => 'getweb_option_menus',				'callback'    => array( $this->callbacks, 'menu_view' )			),			array(				'parent_slug' => 'getweb_theme_options',				'page_title'  => 'Add Menu',				'menu_title'  => '',				'capability'  => 'manage_options',				'menu_slug'   => 'getweb_new_menu',				'callback'    => array( $this->callbacks, 'menu_new' )			),			array(				'parent_slug' => 'getweb_theme_options',				'page_title'  => 'Edit Menu',				'menu_title'  => '',				'capability'  => 'manage_options',				'menu_slug'   => 'getweb_edit_menu',				'callback'    => array( $this->callbacks, 'menu_edit' )			),		);	}}