<?phpglobal $wpdb;$get_fields = $wpdb->get_results(	$wpdb->prepare(		"SELECT * from " . getweb_field_table() . " ORDER by id ASC", ""	), ARRAY_A);?><div class="container-fluid mrt50">  <div class="panel panel-border">    <div class="clearfix panel-custom-color">      <div id="of-popup-save" class="of-save-popup">        <div class="of-save-save"><i class="fa fa-check"></i> <?php esc_html_e( 'Data Deleted', 'rs_russell' ); ?></div>      </div>      <div id="of-popup-fail" class="of-save-popup">        <div class="of-save-fail"><i class="icon-remove"></i> <?php esc_html_e( 'Error! Not Saved', 'rs_russell' ); ?></div>      </div>      <div class="panel-heading pull-left">All Fields</div>      <div class="panel-heading pull-right"><a href="admin.php?page=getweb_new_field" class="btn btn-color">Add New</a></div>    </div>    <div class="panel-body">      <table id="theme_option_table" class="table table-striped table-hover table-bordered" style="width:100%">        <thead>        <tr>          <th>ID</th>          <th>Title</th>          <th>Menu</th>          <th>Data ID</th>          <th>Default Data</th>          <th>Type</th>          <th>Status</th>          <th>Time</th>          <th>Action</th>        </tr>        </thead>        <tbody>		<?php if ( isset( $get_fields ) && count( $get_fields ) > 0 ): ?>			<?php $i = 1;			foreach ( $get_fields as $row ): ?>              <tr>                <td><?php echo $i ++; ?></td>                <td><?php echo $row['title']; ?></td>                <td><?php echo menu_name_by_id( $row['menu_id'] ); ?></td>                <td><?php					if ( $row['type'] == 'select_google_font' ) {						echo $row['get_id'] . '_g_face' . '<br>';						echo $row['get_id'] . '_size' . '<br>';						echo $row['get_id'] . '_style' . '<br>';						echo $row['get_id'] . '_face' . '<br>';						echo $row['get_id'] . '_color' . '<br>';					} elseif ( $row['type'] == 'images' ) {						$j           = 1;						$more_images = json_decode( $row['more_values'] );						foreach ( $more_images as $key => $option ) {							$j ++;							echo $row['get_id'] . '_' . $key. '<br>';						}					} else {						echo $row['get_id'];					} ?></td>                <td><?php					if ( ! empty( $row['default_value'] ) ) {						echo $row['default_value'];					} else {						echo 'No Default Value';					}					?>                </td>                <td><?php echo $row['type']; ?></td>                <td>					<?php if ( ! empty( $row['status'] == 1 ) ): ?>                      <button type="button" class="btn btn-primary btn-sm " disabled>Visible</button>					<?php else: ?>                      <button type="button" class="btn btn-danger btn-sm " disabled>Unvisible</button>					<?php endif ?>                </td>                <td><?php echo create_before( $row['created_at'] ); ?></td>                <td>                  <a href="admin.php?page=getweb_edit_field&edit-id=<?php echo $row['id']; ?>" class="btn btn-primary" data-id="<?php echo $row['id']; ?>">Edit</a>                  <a class="btn btn-danger delete_field" href="javascript:void(0)" data-id="<?php echo $row['id']; ?>">Delete</a>                </td>              </tr>			<?php endforeach ?>		<?php endif ?>        </tbody>      </table>    </div>  </div></div>